import React from "react";
import { SafeAreaView, View, Text, FlatList, Image, TouchableOpacity } from "react-native";
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const User = ({ navigation }) => {
    const fitnessCenters = [
        {
            id: 1,
            img: images.promoBanner,
            title: "Fit Gym",
            description: "Get fit with our expert trainers!"
        },
        {
            id: 2,
            img: images.promoBanner,
            title: "Yoga Studio",
            description: "Find your zen with our yoga classes!"
        },
        {
            id: 3,
            img: images.promoBanner,
            title: "Dance Academy",
            description: "Groove to the rhythm with our dance sessions!"
        },
        {
            id: 4,
            img: images.promoBanner,
            title: "Swimming Pool",
            description: "Cool off and stay fit with our swimming facilities!"
        },
    ];

    function renderHeader() {
        return (
            <View style={{ flexDirection: 'row', marginVertical: SIZES.padding * 5, paddingHorizontal: SIZES.padding * 3 }}>
                <TouchableOpacity
                    style={{
                        width: 45,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={{
                            height: 20,
                            width: 20,
                            tintColor: COLORS.black
                        }}
                    />
                </TouchableOpacity>

                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                    <Text style={{ color: COLORS.black, ...FONTS.h3 }}>User</Text>
                </View>

                <TouchableOpacity
                    style={{
                        width: 45,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                    onPress={() => console.log("Settings")}
                >
                    <Image
                        source={icons.settings}
                        style={{
                            height: 20,
                            width: 20,
                            tintColor: COLORS.black
                        }}
                    />
                </TouchableOpacity>
            </View>
        );
    }

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={{
                marginVertical: SIZES.base,
                width: SIZES.width / 2.5
            }}
            onPress={() => console.log(item.title)}
        >
            <View
                style={{
                    height: 80,
                    borderTopLeftRadius: 20,
                    borderTopRightRadius: 20,
                    backgroundColor: COLORS.primary
                }}
            >
                <Image
                    source={item.img}
                    resizeMode="cover"
                    style={{
                        width: "100%",
                        height: "100%",
                        borderTopLeftRadius: 20,
                        borderTopRightRadius: 20
                    }}
                />
            </View>

            <View
                style={{
                    padding: SIZES.padding,
                    backgroundColor: COLORS.lightGray,
                    borderBottomLeftRadius: 20,
                    borderBottomRightRadius: 20
                }}
            >
                <Text style={{ ...FONTS.h4 }}>{item.title}</Text>
                <Text style={{ ...FONTS.body4 }}>{item.description}</Text>
                </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={{ flex: 1, backgroundColor: COLORS.white }}>
            {renderHeader()}
            <FlatList
                contentContainerStyle={{ paddingHorizontal: SIZES.padding * 3 }}
                numColumns={2}
                columnWrapperStyle={{ justifyContent: 'space-between' }}
                data={fitnessCenters}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={
                    <View style={{ marginBottom: 80 }}></View>
                }
            />
        </SafeAreaView>
    );
};

export default User;
