import React, { useState, useEffect } from "react";
import { SafeAreaView, View, Text, TouchableOpacity, StyleSheet, ActivityIndicator, Alert, Image, TextInput } from "react-native";
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import { COLORS, SIZES, FONTS, images } from "../../../constants";
import axios from 'axios';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const OrderCreate = ({ route }) => {
    const { meal, canteenName } = route.params;
    const navigation = useNavigation();
    const [loading, setLoading] = useState(false);
    const [csrfToken, setCsrfToken] = useState('');
    const [quantity, setQuantity] = useState(1); // Default quantity

    // Fetch CSRF Token
    useEffect(() => {
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/csrf/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token', error);
            }
        };

        fetchCsrfToken();
    }, []);

    const handleOrder = async () => {
        setLoading(true);
        try {
            const accessToken = await AsyncStorage.getItem('accessToken');
            if (!accessToken || !csrfToken) {
                console.error('Access token or CSRF token not available');
                return;
            }

            const response = await axios.post(`http://127.0.0.1:8000/create-order/`, 
                { 
                    food: meal.id, 
                    quantity: quantity 
                }, 
                {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                        'X-CSRFToken': csrfToken,
                        'Content-Type': 'application/json'
                    }
                }
            );

            Alert.alert("Success", "Order created successfully");
            navigation.navigate("OrderList");
        } catch (error) {
            console.error("Error creating order:", error.response.data);
            Alert.alert("Error", "There was an error creating the order.");
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <SafeAreaView style={styles.container}>
                <View style={styles.loader}>
                    <ActivityIndicator size="large" color={COLORS.primary} />
                </View>
            </SafeAreaView>
        );
    }

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Icon name="arrow-left" size={20} color={COLORS.black} />
                </TouchableOpacity>
                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Create Order</Text>
                </View>
            </View>
            <View style={styles.imageContainer}>
                <Image source={images.promoBanner} style={styles.mealImage} />
                <Text style={styles.mealName}>{meal.name}</Text>
                <Text style={styles.mealDescription}>{meal.description}</Text>
            </View>
            <View style={styles.orderSummary}>
                <View style={styles.mealInfo}>
                    <Image source={images.juanbytesLogo} style={styles.smallImage} />
                    <View style={styles.mealDetails}>
                        <Text style={styles.mealName}>{meal.name}</Text>
                        <Text style={styles.mealDescription}>{meal.description}</Text>
                    </View>
                    <Text style={styles.mealPrice}>₱{meal.price}</Text>
                </View>
                <Text style={styles.orderLabel}>Order summary</Text>
                <View style={styles.summaryDetails}>
                    <Text style={styles.summaryText}>Subtotal</Text>
                    <Text style={styles.summaryText}>₱{meal.price}</Text>
                </View>
                <View style={styles.summaryDetails}>
                    <Text style={styles.summaryText}>Standard delivery</Text>
                    <Text style={styles.summaryText}>₱47.00</Text>
                </View>
                <View style={styles.totalContainer}>
                    <Text style={styles.totalLabel}>Total (incl. VAT)</Text>
                    <Text style={styles.totalPrice}>₱{meal.price + 47}</Text>
                </View>
            </View>
            <TouchableOpacity style={styles.orderButton} onPress={handleOrder}>
                <Text style={styles.orderButtonText}>Place order</Text>
            </TouchableOpacity>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    imageContainer: {
        alignItems: 'center',
    },
    mealImage: {
        width: 200,
        height: 200,
        resizeMode: 'contain',
    },
    mealName: {
        ...FONTS.h2,
        color: COLORS.black,
        marginVertical: SIZES.padding,
    },
    mealDescription: {
        ...FONTS.body3,
        color: COLORS.gray,
        textAlign: 'center',
        marginVertical: SIZES.padding,
    },
    orderSummary: {
        paddingHorizontal: SIZES.padding * 3,
    },
    mealInfo: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: SIZES.padding,
    },
    smallImage: {
        width: 50,
        height: 50,
        resizeMode: 'contain',
    },
    mealDetails: {
        flex: 1,
        marginLeft: SIZES.padding,
    },
    mealPrice: {
        ...FONTS.h2,
        color: COLORS.black,
    },
    orderLabel: {
        ...FONTS.h2,
        color: COLORS.black,
        marginVertical: SIZES.padding,
    },
    summaryDetails: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginBottom: SIZES.padding,
    },
    summaryText: {
        ...FONTS.body3,
        color: COLORS.gray,
    },
    totalContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginVertical: SIZES.padding,
    },
    totalLabel: {
        ...FONTS.h2,
        color: COLORS.black,
    },
    totalPrice: {
        ...FONTS.h2,
        color: COLORS.black,
    },
    orderButton: {
        backgroundColor: COLORS.primary,
        borderRadius: 10,
        paddingVertical: SIZES.padding * 1.5,
        paddingHorizontal: SIZES.padding * 3,
        marginTop: SIZES.padding * 2,
        alignItems: 'center',
    },
    orderButtonText: {
        color: COLORS.white,
        ...FONTS.h3,
    },
    loader: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});

export default OrderCreate;
