import React from "react";
import { SafeAreaView, View, Text, FlatList, TouchableOpacity, StyleSheet, Image } from "react-native";
import { COLORS, SIZES, FONTS, icons } from "../constants";

const Notif = ({ navigation }) => {
    // Sample data for notifications
    const notifications = [
        {
            id: '1',
            title: 'New Feature Released!',
            description: 'Check out the latest updates and improvements in the app.',
            date: '2024-07-20',
        },
        {
            id: '2',
            title: 'Security Alert',
            description: 'We detected unusual activity in your account. Please review your recent actions.',
            date: '2024-07-18',
        },
        {
            id: '3',
            title: 'Maintenance Scheduled',
            description: 'The app will be down for maintenance on 2024-07-25 from 2 AM to 4 AM.',
            date: '2024-07-15',
        },
        // Add more notifications as needed
    ];

    function renderHeader() {
        return (
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={styles.icon}
                    />
                </TouchableOpacity>

                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Notifications</Text>
                </View>

                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => console.log("Settings")}
                >
                    <Image
                        source={icons.settings}
                        style={styles.icon}
                    />
                </TouchableOpacity>
            </View>
        );
    }

    const renderItem = ({ item }) => (
        <View style={styles.notificationItem}>
            <Text style={styles.notificationTitle}>{item.title}</Text>
            <Text style={styles.notificationDescription}>{item.description}</Text>
            <Text style={styles.notificationDate}>{item.date}</Text>
        </View>
    );

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            <FlatList
                data={notifications}
                keyExtractor={item => item.id}
                renderItem={renderItem}
                contentContainerStyle={styles.listContainer}
                showsVerticalScrollIndicator={false}
            />
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    icon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black,
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    listContainer: {
        paddingHorizontal: SIZES.padding * 3,
    },
    notificationItem: {
        marginVertical: SIZES.base,
        padding: SIZES.padding,
        backgroundColor: COLORS.lightGray,
        borderRadius: 10,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 3 },
        shadowOpacity: 0.2,
        shadowRadius: 4.65,
        elevation: 3,
    },
    notificationTitle: {
        ...FONTS.h4,
        color: COLORS.black,
    },
    notificationDescription: {
        ...FONTS.body4,
        color: COLORS.gray,
        marginVertical: SIZES.base / 2,
    },
    notificationDate: {
        ...FONTS.body5,
        color: COLORS.gray,
    },
});

export default Notif;
