import React from "react";
import { SafeAreaView, View, Text, TouchableOpacity, Image, StyleSheet } from "react-native";
import { COLORS, FONTS, icons, SIZES } from "../constants";

const Wallet = ({ navigation }) => {
    // User balance
    const userBalance = "46,072.55";

    // Header component
    function renderHeader() {
        return (
            <View style={{ flexDirection: 'row', marginVertical: SIZES.padding * 5, paddingHorizontal: SIZES.padding * 3, alignItems: 'center' }}>
                <TouchableOpacity
                    style={{ width: 45, alignItems: 'center', justifyContent: 'center' }}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={{ height: 20, width: 20, tintColor: COLORS.black }}
                    />
                </TouchableOpacity>

                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                    <Text style={{ color: COLORS.black, ...FONTS.h3 }}>Wallet</Text>
                </View>

                <TouchableOpacity
                    style={{ width: 45, alignItems: 'center', justifyContent: 'center' }}
                    onPress={() => console.log("Settings")}
                >
                    <Image
                        source={icons.settings}
                        style={{ height: 20, width: 20, tintColor: COLORS.black }}
                    />
                </TouchableOpacity>
            </View>
        );
    }

    // User balance component
    function renderUserBalance() {
        return (
            <View
                style={{
                    height: 120,
                    borderRadius: 20,
                    backgroundColor: COLORS.lightGray,
                    paddingHorizontal: SIZES.padding * 2,
                    paddingVertical: SIZES.padding,
                    marginHorizontal: SIZES.padding * 2,
                    flexDirection: 'row',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    shadowColor: "#000",
                    shadowOffset: {
                        width: 0,
                        height: 3,
                    },
                    shadowOpacity: 0.27,
                    shadowRadius: 4.65,
                    elevation: 6,
                }}
            >
                <View>
                    <Text style={{ ...FONTS.body3, color: COLORS.gray }}>Available Balance</Text>
                    <Text style={{ ...FONTS.h1 }}>â‚±{userBalance}</Text>
                </View>
                <View>
                    <TouchableOpacity
                        style={{
                            width: 40,
                            height: 40,
                            borderRadius: 20,
                            backgroundColor: COLORS.primary,
                            justifyContent: 'center',
                            alignItems: 'center'
                        }}
                        onPress={() => navigation.navigate("SendMoney")}
                    >
                        <Image
                            source={icons.send}
                            resizeMode="contain"
                            style={{
                                width: 20,
                                height: 20,
                                tintColor: COLORS.white
                            }}
                        />
                    </TouchableOpacity>
                </View>
            </View>
        );
    }

    return (
        <SafeAreaView style={{ flex: 1, backgroundColor: COLORS.white }}>
            {renderHeader()}
            {renderUserBalance()}
            <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                <Text style={{ color: COLORS.gray, ...FONTS.body3 }}></Text>
                {/* You can add more content here as needed */}
            </View>
        </SafeAreaView>
    );
};



export default Wallet;
