// screens/MainCourse.js

import React from "react";
import { SafeAreaView, View, Text, FlatList, TouchableOpacity, StyleSheet } from "react-native";
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import { COLORS, SIZES, FONTS } from "../../../constants";

const MainCourse = ({ navigation }) => {
    const meals = [
        {
            id: 1,
            icon: 'food',
            title: "Meal 1",
            description: "Delicious meal 1 description.",
        },
        {
            id: 2,
            icon: 'food-variant',
            title: "Meal 2",
            description: "Tasty meal 2 description.",
        },
        {
            id: 3,
            icon: 'food-fork-drink',
            title: "Meal 3",
            description: "Yummy meal 3 description.",
        },
        {
            id: 4,
            icon: 'food-off',
            title: "Meal 4",
            description: "Savory meal 4 description.",
        },
    ];

    function renderHeader() {
        return (
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Icon name="arrow-left" size={20} color={COLORS.black} />
                </TouchableOpacity>
                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Main Course in APS</Text>
                </View>
               
            </View>
        );
    }

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={styles.cardContainer}
        >
            <View style={styles.iconContainer}>
                <Icon name={item.icon} size={50} color={COLORS.primary} />
            </View>
            <View style={styles.textContainer}>
                <Text style={styles.cardTitle}>{item.title}</Text>
                <Text style={styles.cardDescription}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            <FlatList
                contentContainerStyle={styles.listContainer}
                numColumns={2}
                columnWrapperStyle={styles.columnWrapper}
                data={meals}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={<View style={styles.footer} />}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    listContainer: {
        paddingHorizontal: SIZES.padding * 3,
    },
    columnWrapper: {
        justifyContent: 'space-between',
    },
    cardContainer: {
        marginVertical: SIZES.base,
        width: SIZES.width / 2.5,
        borderRadius: 20,
        overflow: 'hidden',
        elevation: 3,
        backgroundColor: COLORS.white,
        alignItems: 'center',
        justifyContent: 'center',
        padding: SIZES.padding,
    },
    iconContainer: {
        marginBottom: SIZES.base,
    },
    textContainer: {
        alignItems: 'center',
    },
    cardTitle: {
        ...FONTS.h4,
        color: COLORS.black,
        marginBottom: SIZES.base / 2,
    },
    cardDescription: {
        ...FONTS.body4,
        color: COLORS.gray,
        textAlign: 'center',
    },
    footer: {
        marginBottom: 80,
    },
});

export default MainCourse;
