// screens/Food.js
import React from "react";
import { SafeAreaView, View, Text, FlatList, Image, TouchableOpacity } from "react-native";
import { COLORS, SIZES, FONTS, images } from "../constants";

const Food = () => {
    const canteens = [
        {
            id: 1,
            img: images.promoBanner,
            title: "APS Canteen",
            description: "Don't miss it. Grab now!"
        },
        {
            id: 2,
            img: images.promoBanner,
            title: "PGN Canteen",
            description: "Don't miss it. Grab now!"
        },
        {
            id: 3,
            img: images.promoBanner,
            title: "GGN Canteen",
            description: "Don't miss it. Grab now!"
        },
        {
            id: 4,
            img: images.promoBanner,
            title: "Yellow Canteen",
            description: "Don't miss it. Grab now!"
        },
    ];

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={{
                marginVertical: SIZES.base,
                width: SIZES.width / 2.5
            }}
            onPress={() => console.log(item.title)}
        >
            <View
                style={{
                    height: 80,
                    borderTopLeftRadius: 20,
                    borderTopRightRadius: 20,
                    backgroundColor: COLORS.primary
                }}
            >
                <Image
                    source={item.img}
                    resizeMode="cover"
                    style={{
                        width: "100%",
                        height: "100%",
                        borderTopLeftRadius: 20,
                        borderTopRightRadius: 20
                    }}
                />
            </View>

            <View
                style={{
                    padding: SIZES.padding,
                    backgroundColor: COLORS.lightGray,
                    borderBottomLeftRadius: 20,
                    borderBottomRightRadius: 20
                }}
            >
                <Text style={{ ...FONTS.h4 }}>{item.title}</Text>
                <Text style={{ ...FONTS.body4 }}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={{ flex: 1, backgroundColor: COLORS.white }}>
            <FlatList
                contentContainerStyle={{ paddingHorizontal: SIZES.padding * 3, paddingBottom: 80 }}
                numColumns={2}
                columnWrapperStyle={{ justifyContent: 'space-between' }}
                data={canteens}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
            />
        </SafeAreaView>
    );
}

export default Food;
