import React, { useState, useEffect } from "react";
import {
    SafeAreaView,
    View,
    Text,
    Image,
    FlatList,
    TouchableOpacity
} from "react-native"
import { COLORS, SIZES, FONTS, icons, images } from "../constants"

import { useNavigation, useFocusEffect } from '@react-navigation/native';

import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Home = () => {
    const navigation = useNavigation();
    const [hasUnreadNotifications, setHasUnreadNotifications] = useState(true);

    const featuresData = [
        {
            id: 1,
            icon: icons.reload,
            color: COLORS.primary,
            backgroundColor: COLORS.white,
            description: "Top Up",
            screen: "TopUp"
        },
        {
            id: 2,
            icon: icons.food,
            color: COLORS.yellow,
            backgroundColor: COLORS.lightyellow,
            description: "Foods",
            screen: "Food"
        },
        {
            id: 3,
            icon: icons.food,
            color: COLORS.primary,
            backgroundColor: COLORS.lightGreen,
            description: "My Fitness",
            screen: "MyFitness"
        },
        {
            id: 4,
            icon: icons.wallet,
            color: COLORS.red,
            backgroundColor: COLORS.lightRed,
            description: "Wallet",
            screen: "Wallet"
        },
        {
            id: 5,
            icon: icons.bill,
            color: COLORS.yellow,
            backgroundColor: COLORS.lightyellow,
            description: "Transactions",
            screen: "Transactions"
        },
    ]

    const specialPromoData = [
        {
            id: 1,
            img: images.promoBanner,
            title: "Soy Burger",
            description: "Buy now!"
        },
        {
            id: 2,
            img: images.promoBanner,
            title: "Popcorn",
            description: "Buy now!"
        },
        {
            id: 3,
            img: images.promoBanner,
            title: "Mami",
            description: "Buy now!"
        },
        {
            id: 4,
            img: images.promoBanner,
            title: "Snacks",
            description: "Buy now!"
        },
    ]

    const [features, setFeatures] = React.useState(featuresData)
    const [specialPromos, setSpecialPromos] = React.useState(specialPromoData)
    const [balance, setBalance] = React.useState(null);
    const [loading, setLoading] = React.useState(true);
    const [csrfToken, setCsrfToken] = useState('');
    const [firstName, setFirstName] = useState(''); // State for first name

    // Fetch CSRF Token
    useEffect(() => {
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/csrf/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token', error);
            }
        };

        fetchCsrfToken();
    }, []); // Empty dependency array to ensure this runs only once

    // Fetch Balance and User Information
    const fetchBalance = async () => {
        try {
            const accessToken = await AsyncStorage.getItem('accessToken');
            if (!accessToken || !csrfToken) {
                console.error('Token or CSRF Token not available');
                return;
            }

            const response = await axios.get('http://127.0.0.1:8000/balance/', {
                headers: {
                    'Authorization': `Bearer ${accessToken}`,
                    'X-CSRFToken': csrfToken
                }
            });

            // Convert balance to float and format
            const balance = parseFloat(response.data.balance).toFixed(2);
            const formattedBalance = Number(balance).toLocaleString();
            setBalance(formattedBalance);

            // Capitalize the first letter of the first name
            const firstName = response.data.first_name;
            const capitalizedFirstName = firstName.charAt(0).toUpperCase() + firstName.slice(1);
            setFirstName(capitalizedFirstName);
        } catch (error) {
            console.error("Error fetching balance:", error);
            Alert.alert('Error', 'Unable to fetch balance.');
        }
    };

    useFocusEffect(
        React.useCallback(() => {
            if (csrfToken) {
                fetchBalance();
            }
        }, [csrfToken])
    );

    useEffect(() => {
        const checkNotifications = async () => {
            const notificationsRead = await AsyncStorage.getItem('notificationsRead');
            setHasUnreadNotifications(notificationsRead !== 'true');
        };

        checkNotifications();
    }, []);

    const handleNotificationsPress = async () => {
        await AsyncStorage.setItem('notificationsRead', 'true');
        setHasUnreadNotifications(false);
        navigation.navigate("Notif");
    };

    function renderHeader() {
        return (
            <View style={{ flexDirection: 'row', marginVertical: SIZES.padding * 3 }}>
                <View style={{ flex: 1 }}>
                    <Image
                        source={images.juanbytesLogo} 
                        resizeMode="contain" 
                        style={{
                            width: 100,  
                            height: 100, 
                        }}
                    />
                </View>

                <View style={{ alignItems: 'center', justifyContent: 'center' }}>
                    <TouchableOpacity
                        style={{
                            height: 40,
                            width: 40,
                            justifyContent: 'center',
                            alignItems: 'center',
                            backgroundColor: COLORS.lightGray
                        }}
                        onPress={handleNotificationsPress}
                    >
                        <Image
                            source={icons.bell}
                            style={{
                                width: 20,
                                height: 20,
                                tintColor: COLORS.secondary
                            }}
                        />
                        {hasUnreadNotifications && (
                            <View
                                style={{
                                    position: 'absolute',
                                    top: -5,
                                    right: -5,
                                    height: 10,
                                    width: 10,
                                    backgroundColor: COLORS.red,
                                    borderRadius: 5
                                }}
                            >
                            </View>
                        )}
                    </TouchableOpacity>
                </View>
            </View>
        )
    }

    function renderUserBalance() {
        return (
            <View
                style={{
                    height: 100,
                    borderRadius: 30,
                    backgroundColor: COLORS.lightGray,
                    paddingHorizontal: SIZES.padding * 3,
                    paddingVertical: SIZES.padding,
                    marginHorizontal: SIZES.padding,
                    marginTop: SIZES.padding, // Adjusted margin
                    flexDirection: 'row',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    shadowColor: "#000",
                    shadowOffset: {
                        width: 0,
                        height: 3,
                    },
                    shadowOpacity: 0.27,
                    shadowRadius: 4.65,
                    elevation: 6,
                }}
            >
                <View>
                    <Text style={{ ...FONTS.body3, color: COLORS.gray }}>Available Balance</Text>
                    <Text style={{ ...FONTS.h2 }}>₱{balance}</Text>
                </View>
                <View>
                    <TouchableOpacity
                        style={{
                            width: 35,
                            height: 35,
                            borderRadius: 20,
                            backgroundColor: COLORS.lightorrange,
                            justifyContent: 'center',
                            alignItems: 'center'
                        }}
                        onPress={() => navigation.navigate("SendMoney")}
                    >
                        <Image
                            source={icons.send}
                            resizeMode="contain"
                            style={{
                                width: 20,
                                height: 20,
                                tintColor: COLORS.white
                            }}
                        />
                    </TouchableOpacity>
                </View>
            </View>
        )
    }

    function renderFeatures() {
        const Header = () => (
            <View style={{ marginBottom: SIZES.padding }}>
                <Text style={{ ...FONTS.h3 }}>Features</Text>
            </View>
        )

        const renderItem = ({ item }) => (
            <TouchableOpacity
                style={{ marginBottom: SIZES.padding , width: 60, alignItems: 'center' }}
                onPress={() => {
                    if (item.screen) {
                        navigation.navigate(item.screen);
                    } else {
                        console.log(item.description);
                    }
                }}
            >
                <View
                    style={{
                        height: 50,
                        width: 50,
                        marginBottom: 5,
                        borderRadius: 20,
                        backgroundColor: item.backgroundColor,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                >
                    <Image
                        source={item.icon}
                        resizeMode="contain"
                        style={{
                            height: 20,
                            width: 20,
                            tintColor: item.color
                        }}
                    />
                </View>
                <Text style={{ textAlign: 'center', flexWrap: 'wrap', ...FONTS.body4 }}>{item.description}</Text>
            </TouchableOpacity>
        )

        return (
            <FlatList
                ListHeaderComponent={Header}
                data={features}
                numColumns={4}
                columnWrapperStyle={{ justifyContent: 'space-between' }}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                style={{ marginTop: SIZES.padding }}
            />
        )
    }

    // Commenting out renderPromos and its related code for now
    // function renderPromos() {
    //     return (
    //         <View style={{ marginTop: SIZES.padding * 3 }}>
    //             <Text style={{ ...FONTS.h3, marginBottom: SIZES.padding }}>Special Promotions</Text>
    //             <FlatList
    //                 data={specialPromos}
    //                 horizontal
    //                 showsHorizontalScrollIndicator={false}
    //                 keyExtractor={item => `${item.id}`}
    //                 renderItem={({ item }) => (
    //                     <TouchableOpacity
    //                         style={{
    //                             height: 120,
    //                             width: 200,
    //                             marginRight: SIZES.padding,
    //                             borderRadius: 20,
    //                             backgroundColor: COLORS.lightGray,
    //                             overflow: 'hidden'
    //                         }}
    //                         onPress={() => navigation.navigate("PromoDetail", { promo: item })}
    //                     >
    //                         <Image
    //                             source={item.img}
    //                             resizeMode="cover"
    //                             style={{
    //                                 height: '100%',
    //                                 width: '100%'
    //                             }}
    //                         />
    //                         <View
    //                             style={{
    //                                 position: 'absolute',
    //                                 bottom: 0,
    //                                 left: 0,
    //                                 right: 0,
    //                                 height: 50,
    //                                 backgroundColor: COLORS.white,
    //                                 justifyContent: 'center',
    //                                 paddingHorizontal: SIZES.padding
    //                             }}
    //                         >
    //                             <Text style={{ ...FONTS.h4 }}>{item.title}</Text>
    //                             <Text style={{ ...FONTS.body4 }}>{item.description}</Text>
    //                         </View>
    //                     </TouchableOpacity>
    //                 )}
    //             />
    //         </View>
    //     )
    // }

    return (
        <SafeAreaView style={{ flex: 1 }}>
            {renderHeader()}
            {renderUserBalance()}
            {renderFeatures()}
            {/* renderPromos() */}
        </SafeAreaView>
    )
}

export default Home;
