import React, { useState, useEffect } from "react";
import { SafeAreaView, View, Text, TextInput, TouchableOpacity, StyleSheet, Image, Alert } from "react-native";
import axios from 'axios';
import { COLORS, SIZES, FONTS, icons } from "../constants";
import AsyncStorage from '@react-native-async-storage/async-storage';

const TopUp = ({ navigation }) => {
    const [depositAmount, setDepositAmount] = useState('');
    const [paymentAmount, setPaymentAmount] = useState('0.00');
    const [feeAmount, setFeeAmount] = useState('0.00');
    const [csrfToken, setCsrfToken] = useState('');
    const [accessToken, setAccessToken] = useState('');
    const [topUpStatus, setTopUpStatus] = useState(null);
    const [balance, setBalance] = useState(null);
    const [loading, setLoading] = useState(false);
    const [isApproved, setIsApproved] = useState(false); // New state to track approval status

    useEffect(() => {
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/csrf/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token', error);
            }
        };

        const fetchAccessToken = async () => {
            try {
                const token = await AsyncStorage.getItem('accessToken');
                setAccessToken(token);
            } catch (error) {
                console.error('Error fetching access token', error);
            }
        };

        fetchCsrfToken();
        fetchAccessToken();
    }, []);

    useEffect(() => {
        const fetchBalance = async () => {
            if (!accessToken || !csrfToken) return;

            try {
                const response = await axios.get('http://127.0.0.1:8000/balance/', {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                        'X-CSRFToken': csrfToken,
                    },
                });

                const balance = parseFloat(response.data.balance).toFixed(2);
                const formattedBalance = Number(balance).toLocaleString();
                setBalance(formattedBalance);
            } catch (error) {
                console.error("Error fetching balance:", error);
                Alert.alert('Error', 'Unable to fetch balance.');
            }
        };

        fetchBalance();
    }, [csrfToken, accessToken]);

    const handleTopUp = async () => {
        if (!accessToken || !csrfToken) {
            Alert.alert('Error', 'Tokens not available');
            return;
        }

        setLoading(true); // Disable the button and show loading state

        try {
            const response = await axios.post(
                'http://127.0.0.1:8000/top-up-requests/',
                {
                    amount: parseFloat(depositAmount),
                },
                {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                        'X-CSRFToken': csrfToken,
                        'Content-Type': 'application/json',
                    },
                }
            );
            const { is_approved } = response.data;
            setIsApproved(is_approved); // Set the approval status
            setTopUpStatus(is_approved ? "Top-up request approved. Please pay at the finance office." : "Top-up request submitted. Awaiting approval.");
            Alert.alert('Success', 'Top-up request submitted.');
        } catch (error) {
            console.error('Error submitting top-up request:', error);
            Alert.alert('Error', 'Unable to submit top-up request.');
        } finally {
            setLoading(false); // Re-enable the button
        }
    };

    function renderHeader() {
        return (
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={styles.icon}
                    />
                </TouchableOpacity>

                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Top Up</Text>
                </View>

                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => console.log("Settings")}
                >
                    <Image
                        source={icons.settings}
                        style={styles.icon}
                    />
                </TouchableOpacity>
            </View>
        );
    }

    function renderTopUpForm() {
        const feePercentage = 0.05;

        return (
            <View style={styles.formContainer}>
                <TextInput
                    style={styles.input}
                    placeholder="0.00"
                    keyboardType="numeric"
                    value={depositAmount}
                    onChangeText={text => {
                        const amount = parseFloat(text) || 0;
                        const fee = amount * feePercentage;
                        setDepositAmount(text);
                        setFeeAmount(fee.toFixed(2));
                        setPaymentAmount((amount + fee).toFixed(2));
                    }}
                />

                <View style={styles.amountContainer}>
                    <View>
                        <Text style={styles.amountLabel}>Fee:</Text>
                        <Text style={styles.amountLabel}>Total Amount to Pay:</Text>
                    </View>
                    <View>
                        <Text style={styles.amountValue}>₱{feeAmount || "0.00"}</Text>
                        <Text style={styles.amountValue}>₱{paymentAmount || "0.00"}</Text>
                    </View>
                </View>

                {topUpStatus && (
                    <View style={styles.statusContainer}>
                        <Text style={styles.statusText}>{topUpStatus}</Text>
                    </View>
                )}

                <TouchableOpacity
                    style={[styles.button, loading || !isApproved ? styles.buttonDisabled : {}]} // Apply disabled style if loading or not approved
                    onPress={handleTopUp}
                    disabled={loading || !isApproved} // Disable button if loading or not approved
                >
                    <Text style={styles.buttonText}>{loading ? "Processing..." : isApproved ? "Pay" : "Awaiting Approval"}</Text>
                </TouchableOpacity>
            </View>
        );
    }

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            <View style={styles.balanceContainer}>
                <Text style={styles.balanceLabel}>Available Balance:</Text>
                <Text style={styles.balanceValue}>₱{balance || "0.00"}</Text>
            </View>
            {renderTopUpForm()}
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    icon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black,
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    formContainer: {
        flex: 1,
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
    },
    input: {
        height: 50,
        borderColor: COLORS.gray,
        borderWidth: 1,
        borderRadius: 10,
        marginBottom: SIZES.base,
        paddingHorizontal: SIZES.padding,
        fontSize: SIZES.body3,
        color: COLORS.black,
    },
    amountContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginVertical: SIZES.base,
        padding: SIZES.padding,
        borderRadius: 10,
        backgroundColor: COLORS.lightGray,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 3 },
        shadowOpacity: 0.27,
        shadowRadius: 4.65,
        elevation: 6,
    },
    amountLabel: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    amountValue: {
        ...FONTS.h4,
        color: COLORS.black,
    },
    button: {
        backgroundColor: COLORS.primary,
        paddingVertical: SIZES.base,
        borderRadius: 10,
        alignItems: 'center',
    },
    buttonDisabled: {
        backgroundColor: COLORS.gray,
    },
    buttonText: {
        color: COLORS.white,
        ...FONTS.h4,
    },
    statusContainer: {
        marginVertical: SIZES.base,
        padding: SIZES.padding,
        borderRadius: 10,
        backgroundColor: COLORS.lightGray,
        alignItems: 'center',
    },
    statusText: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    balanceContainer: {
        paddingHorizontal: SIZES.padding * 3,
        paddingVertical: SIZES.padding,
        borderBottomWidth: 1,
        borderBottomColor: COLORS.gray,
        backgroundColor: COLORS.lightGray,
        alignItems: 'center',
    },
    balanceLabel: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    balanceValue: {
        ...FONTS.h4,
        color: COLORS.black,
    },
});

export default TopUp;
