import React from "react";
import { SafeAreaView, View, Text, FlatList, Image, TouchableOpacity, StyleSheet } from "react-native";
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const Wallet = ({ navigation }) => {
    const fitnessCenters = [
        {
            id: 1,
            img: images.promoBanner,
            title: "Fit Gym",
            description: "Get fit with our expert trainers!"
        },
        {
            id: 2,
            img: images.promoBanner,
            title: "Yoga Studio",
            description: "Find your zen with our yoga classes!"
        },
        {
            id: 3,
            img: images.promoBanner,
            title: "Dance Academy",
            description: "Groove to the rhythm with our dance sessions!"
        },
        {
            id: 4,
            img: images.promoBanner,
            title: "Swimming Pool",
            description: "Cool off and stay fit with our swimming facilities!"
        },
    ];

    const renderHeader = () => (
        <View style={styles.headerContainer}>
            <TouchableOpacity
                style={styles.headerButton}
                onPress={() => navigation.goBack()}
            >
                <Image
                    source={icons.back}
                    style={styles.headerIcon}
                />
            </TouchableOpacity>

            <View style={styles.headerTitleContainer}>
                <Text style={styles.headerTitle}>Wallet</Text>
            </View>

            <TouchableOpacity
                style={styles.headerButton}
                onPress={() => console.log("Settings")}
            >
                <Image
                    source={icons.settings}
                    style={styles.headerIcon}
                />
            </TouchableOpacity>
        </View>
    );

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={styles.itemContainer}
            onPress={() => console.log(item.title)}
        >
            <View style={styles.itemImageContainer}>
                <Image
                    source={item.img}
                    resizeMode="cover"
                    style={styles.itemImage}
                />
            </View>

            <View style={styles.itemInfo}>
                <Text style={styles.itemTitle}>{item.title}</Text>
                <Text style={styles.itemDescription}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={styles.safeArea}>
            {renderHeader()}
            <FlatList
                contentContainerStyle={styles.listContainer}
                numColumns={2}
                columnWrapperStyle={styles.columnWrapper}
                data={fitnessCenters}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={<View style={styles.footer} />}
            />
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    safeArea: {
        flex: 1,
        backgroundColor: COLORS.white
    },
    headerContainer: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center'
    },
    headerButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center'
    },
    headerIcon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black
    },
    headerTitleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center'
    },
    headerTitle: {
        color: COLORS.black,
        ...FONTS.h3
    },
    itemContainer: {
        marginVertical: SIZES.base,
        width: SIZES.width / 2.5
    },
    itemImageContainer: {
        height: 80,
        borderTopLeftRadius: 20,
        borderTopRightRadius: 20,
        backgroundColor: COLORS.primary
    },
    itemImage: {
        width: '100%',
        height: '100%',
        borderTopLeftRadius: 20,
        borderTopRightRadius: 20
    },
    itemInfo: {
        padding: SIZES.padding,
        backgroundColor: COLORS.lightGray,
        borderBottomLeftRadius: 20,
        borderBottomRightRadius: 20
    },
    itemTitle: {
        ...FONTS.h4
    },
    itemDescription: {
        ...FONTS.body4
    },
    listContainer: {
        paddingHorizontal: SIZES.padding * 3
    },
    columnWrapper: {
        justifyContent: 'space-between'
    },
    footer: {
        marginBottom: 80
    }
});

export default Wallet;
