import React, { useState, useEffect } from "react";
import {
    View,
    Text,
    TouchableOpacity,
    TouchableWithoutFeedback,
    Image,
    TextInput,
    Modal,
    FlatList,
    KeyboardAvoidingView,
    ScrollView,
    Platform,
    Alert
} from "react-native";
import { LinearGradient } from 'expo-linear-gradient';
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const Login = ({ navigation }) => {
    const [showPassword, setShowPassword] = useState(false);
    const [showMpin, setShowMpin] = useState(false);
    const [phoneNumber, setPhoneNumber] = useState("");
    const [password, setPassword] = useState("");
    const [mpin, setMpin] = useState("");
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const [areas, setAreas] = useState([]);
    const [selectedArea, setSelectedArea] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);

    useEffect(() => {
        fetch("https://restcountries.com/v3.1/all?fields=name,flags,cca3,idd")
            .then(response => response.json())
            .then(data => {
                let areaData = data.map(item => ({
                    code: item.cca3,
                    name: item.name?.common,
                    flag: item.flags.png,
                    callingCode: item.idd?.root + item.idd?.suffixes[0],
                }));
                setAreas(areaData);

                // Set default area code to +63 (Philippines)
                let defaultData = areaData.find(a => a.code === "PH");
                if (defaultData) {
                    setSelectedArea(defaultData);
                }
            });
    }, []);

    const handleLogin = () => {
        if (phoneNumber === "09199496960" && password === "123") {
            setIsAuthenticated(true);
        } else {
            Alert.alert("Error", "Invalid phone number or password");
        }
    };

    const handleMpin = () => {
        if (mpin === "1234") {
            navigation.navigate("HomeTabs");
        } else {
            Alert.alert("Error", "Invalid MPIN");
        }
    };

    const renderHeader = () => (
        <TouchableOpacity
            style={{
                flexDirection: 'row',
                alignItems: "center",
                marginTop: SIZES.padding * 6,
                paddingHorizontal: SIZES.padding * 2
            }}
            onPress={() => console.log("Login")}
        >
        </TouchableOpacity>
    );

    const renderLogo = () => (
        <View
            style={{
                marginTop: SIZES.padding * 5,
                height: 100,
                alignItems: 'center',
                justifyContent: 'center'
            }}
        >
            <Image
                source={images.juanbytesLogo}
                resizeMode="contain"
                style={{
                    width: "100%",
                    height: "100%",
                    borderTopLeftRadius: 20,
                    borderTopRightRadius: 20
                }}
            />
        </View>
    );

    function renderForm() {
        return (
            <View
                style={{
                    marginTop: SIZES.padding * 3,
                    marginHorizontal: SIZES.padding * 3,
                }}
            >
                {/* Phone Number */}
                <View style={{ marginTop: SIZES.padding * 2 }}>
                    <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>Phone Number</Text>
    
                    <View style={{ flexDirection: 'row' }}>
                       
    
                        {/* Phone Number */}
                        <TextInput
                            style={{
                                flex: 1,
                                marginVertical: SIZES.padding,
                                borderBottomColor: COLORS.black,
                                borderBottomWidth: 1,
                                height: 40,
                                color: COLORS.black,
                                ...FONTS.body3
                            }}
                            placeholder="Enter Phone Number"
                            placeholderTextColor={COLORS.gray}
                            selectionColor={COLORS.black}
                            onChangeText={(text) => setPhoneNumber(text)}
                        />
                    </View>
                </View>
    
                {/* Password */}
                <View style={{ marginTop: SIZES.padding * 2 }}>
                    <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>Password</Text>
                    <TextInput
                        style={{
                            marginVertical: SIZES.padding,
                            borderBottomColor: COLORS.black,
                            borderBottomWidth: 1,
                            height: 40,
                            color: COLORS.black,
                            ...FONTS.body3
                        }}
                        placeholder="Enter Password"
                        placeholderTextColor={COLORS.gray}
                        selectionColor={COLORS.black}
                        secureTextEntry={!showPassword}
                        onChangeText={(text) => setPassword(text)}
                    />
                    <TouchableOpacity
                        style={{
                            position: 'absolute',
                            right: 0,
                            bottom: 10,
                            height: 30,
                            width: 30
                        }}
                        onPress={() => setShowPassword(!showPassword)}
                    >
                        <Image
                            source={showPassword ? icons.disable_eye : icons.eye}
                            style={{
                                height: 20,
                                width: 20,
                                tintColor: COLORS.black
                            }}
                        />
                    </TouchableOpacity>
                </View>
    
                {/* MPIN */}
                {isAuthenticated && (
                    <View style={{ marginTop: SIZES.padding * 2 }}>
                        <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>MPIN</Text>
                        <View style={{
                            flexDirection: 'row',
                            justifyContent: 'center',
                            alignItems: 'center',
                            marginVertical: SIZES.padding
                        }}>
                            {Array.from({ length: 4 }).map((_, index) => (
                                <View
                                key={index}
                                style={{
                                    width: 40,
                                    height: 40,
                                    borderWidth: 1,
                                    borderColor: COLORS.black,
                                    borderRadius: 5,
                                    marginHorizontal: 5,
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    backgroundColor: COLORS.white
                                }}
                            >
                                <Text style={{
                                    fontSize: 18,
                                    color: COLORS.black,
                                    ...FONTS.body3
                                }}>
                                    {showMpin ? mpin[index] || '' : (mpin[index] ? '*' : '')}
                                </Text>
                            </View>
                            
                            ))}
                            <TouchableOpacity
                                onPress={() => setShowMpin(!showMpin)}
                                style={{
                                    marginLeft: 10
                                }}
                            >
                                <Image
                                    source={showMpin ? icons.disable_eye : icons.eye}
                                    style={{
                                        height: 20,
                                        width: 20,
                                        tintColor: COLORS.black
                                    }}
                                />
                            </TouchableOpacity>
                        </View>
    
                        <TextInput
                            style={{
                                position: 'absolute',
                                opacity: 0,
                                height: 0,
                                width: 0
                            }}
                            value={mpin}
                            onChangeText={(text) => setMpin(text)}
                            onSubmitEditing={handleMpin}
                            keyboardType="numeric"
                            maxLength={4}
                            secureTextEntry={!showMpin}
                        />
    
                        <View style={{ flexDirection: 'row', flexWrap: 'wrap', justifyContent: 'center' }}>
                            {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
                                <TouchableOpacity
                                    key={num}
                                    style={{
                                        width: 60,
                                        height: 60,
                                        margin: 5,
                                        backgroundColor: COLORS.lightGray,
                                        justifyContent: 'center',
                                        alignItems: 'center',
                                        borderRadius: 30
                                    }}
                                    onPress={() => {
                                        if (mpin.length < 4) {
                                            setMpin(mpin + num);
                                        }
                                    }}
                                >
                                    <Text style={{ fontSize: 24, color: COLORS.black }}>{num}</Text>
                                </TouchableOpacity>
                            ))}
                            <TouchableOpacity
                                style={{
                                    width: 60,
                                    height: 60,
                                    margin: 5,
                                    backgroundColor: COLORS.lightGray,
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    borderRadius: 30
                                }}
                                onPress={() => setMpin(mpin.slice(0, -1))} // Remove last digit
                            >
                                <Text style={{ fontSize: 24, color: COLORS.black }}>⌫</Text>
                            </TouchableOpacity>
                            <TouchableOpacity
                                style={{
                                    width: 60,
                                    height: 60,
                                    margin: 5,
                                    backgroundColor: COLORS.lightGray,
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    borderRadius: 30
                                }}
                                onPress={() => {
                                    if (mpin.length === 4) {
                                        handleMpin();
                                    }
                                }}
                            >
                                <Text style={{ fontSize: 24, color: COLORS.black }}>✓</Text>
                            </TouchableOpacity>
                            <TouchableOpacity
                                style={{
                                    width: 60,
                                    height: 60,
                                    margin: 5,
                                    backgroundColor: COLORS.lightGray,
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    borderRadius: 30
                                }}
                                onPress={() => {
                                    if (mpin.length < 4) {
                                        setMpin(mpin + 0);
                                    }
                                }}
                            >
                                <Text style={{ fontSize: 24, color: COLORS.black }}>0</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                )}
            </View>
        );
    }
    
    
    
    const renderButton = () => (
        <View style={{ margin: SIZES.padding * 3 }}>
            {!isAuthenticated ? (
                <TouchableOpacity
                    style={{
                        height: 60,
                        backgroundColor: COLORS.black,
                        borderRadius: SIZES.radius / 1.5,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                    onPress={handleLogin}
                >
                    <Text style={{ color: COLORS.white, ...FONTS.h3 }}>Login</Text>
                </TouchableOpacity>
            ) : (
                <TouchableOpacity
                    style={{
                        height: 60,
                        backgroundColor: COLORS.black,
                        borderRadius: SIZES.radius / 1.5,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                    onPress={handleMpin}
                >
                    <Text style={{ color: COLORS.white, ...FONTS.h3 }}>Enter MPIN</Text>
                </TouchableOpacity>
            )}
        </View>
    );

    const renderSignUpLink = () => (
        <View style={{ margin: SIZES.padding * 3, alignItems: 'center' }}>
            <Text style={{ ...FONTS.body3, lineHeight: SIZES.padding * 1.5 }}>
                Don't have an account?{' '}
                <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
                    <Text style={{ color: COLORS.primary, ...FONTS.body3, lineHeight: SIZES.padding * 1.5 }}>
                        Sign Up
                    </Text>
                </TouchableOpacity>
            </Text>
        </View>
    );
    
    
    const renderAreaCodesModal = () => {
        const renderItem = ({ item }) => (
            <TouchableOpacity
                style={{ padding: SIZES.padding, flexDirection: 'row' }}
                onPress={() => {
                    setSelectedArea(item);
                    setModalVisible(false);
                }}
            >
                <Image
                    source={{ uri: item.flag }}
                    style={{
                        width: 30,
                        height: 30,
                        marginRight: 10
                    }}
                />
                <Text style={{ ...FONTS.body4 }}>{item.name}</Text>
            </TouchableOpacity>
        );

        return (
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
            >
                <TouchableWithoutFeedback
                    onPress={() => setModalVisible(false)}
                >
                                        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                        <View
                            style={{
                                height: 400,
                                width: SIZES.width * 0.8,
                                backgroundColor: COLORS.white,
                                borderRadius: SIZES.radius
                            }}
                        >
                            <FlatList
                                data={areas}
                                renderItem={renderItem}
                                keyExtractor={(item) => item.code}
                                showsVerticalScrollIndicator={false}
                                style={{
                                    padding: SIZES.padding * 2,
                                    marginBottom: SIZES.padding * 2
                                }}
                            />
                        </View>
                    </View>
                </TouchableWithoutFeedback>
            </Modal>
        );
    };

    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === "ios" ? "padding" : null}
            style={{ flex: 1 }}
        >
            <LinearGradient
                colors={[COLORS.white, COLORS.white]}
                style={{ flex: 1 }}
            >
                <ScrollView>
                    {renderHeader()}
                    {renderLogo()}
                    {renderForm()}
                    {renderButton()}
                    {renderSignUpLink()}
                </ScrollView>
            </LinearGradient>
            {renderAreaCodesModal()}
        </KeyboardAvoidingView>
    );
};

export default Login;
