// screens/Food.js

import React from "react";
import { SafeAreaView, View, Text, FlatList, Image, TouchableOpacity, StyleSheet } from "react-native";
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const Food = ({ navigation }) => {
    const canteens = [
        {
            id: 1,
            img: images.promoBanner,
            title: "FoodCategory",
            description: "Buy now, Scan your ID to Pay!"
        },
        {
            id: 2,
            img: images.promoBanner,
            title: "FoodCategory",
            description: "Buy now, Scan your ID to Pay!"
        },
        {
            id: 3,
            img: images.promoBanner,
            title: "FoodCategory",
            description: "Buy now, Scan your ID to Pay!"
        },
        {
            id: 4,
            img: images.promoBanner,
            title: "FoodCategory",
            description: "Buy now, Scan your ID to Pay!"
        },
    ];

    function renderHeader() {
        return (
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={styles.icon}
                    />
                </TouchableOpacity>
                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Choose your Canteen</Text>
                </View>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => console.log("Settings")}
                >
                    <Image
                        source={icons.settings}
                        style={styles.icon}
                    />
                </TouchableOpacity>
            </View>
        );
    }

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={styles.cardContainer}
            onPress={() =>  navigation.navigate(item.title)}
        >
            <View style={styles.imageContainer}>
                <Image
                    source={item.img}
                    resizeMode="cover"
                    style={styles.image}
                />
            </View>
            <View style={styles.textContainer}>
                <Text style={styles.cardTitle}>{item.title}</Text>
                <Text style={styles.cardDescription}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            <FlatList
                contentContainerStyle={styles.listContainer}
                numColumns={2}
                columnWrapperStyle={styles.columnWrapper}
                data={canteens}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={<View style={styles.footer} />}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    icon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black,
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    listContainer: {
        paddingHorizontal: SIZES.padding * 3,
    },
    columnWrapper: {
        justifyContent: 'space-between',
    },
    cardContainer: {
        marginVertical: SIZES.base,
        width: SIZES.width / 2.5,
        borderRadius: 20,
        overflow: 'hidden',
        elevation: 3,
    },
    imageContainer: {
        height: 120,
        borderTopLeftRadius: 20,
        borderTopRightRadius: 20,
        backgroundColor: COLORS.primary,
    },
    image: {
        width: "100%",
        height: "100%",
        borderTopLeftRadius: 20,
        borderTopRightRadius: 20,
    },
    textContainer: {
        padding: SIZES.padding,
        backgroundColor: COLORS.lightGray,
        borderBottomLeftRadius: 20,
        borderBottomRightRadius: 20,
    },
    cardTitle: {
        ...FONTS.h4,
        color: COLORS.black,
    },
    cardDescription: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    footer: {
        marginBottom: 80,
    },
});

export default Food;
