import React from "react";
import { SafeAreaView, View, Text, FlatList, Image, TouchableOpacity } from "react-native";
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const Food = ({ navigation }) => {
    const canteens = [
        {
            id: 1,
            img: images.promoBanner, // Replace with specific canteen images
            title: "APS Canteen",
            description: "Enjoy a variety of delicious meals!"
        },
        {
            id: 2,
            img: images.promoBanner, // Replace with specific canteen images
            title: "PGN Canteen",
            description: "Grab your favorite snacks and meals!"
        },
        {
            id: 3,
            img: images.promoBanner, // Replace with specific canteen images
            title: "GGN Canteen",
            description: "Fresh and tasty options available!"
        },
        {
            id: 4,
            img: images.promoBanner, // Replace with specific canteen images
            title: "Yellow Canteen",
            description: "Diverse menu options for everyone!"
        },
    ];

    function renderHeader() {
        return (
            <View style={{
                flexDirection: 'row',
                alignItems: 'center',
                paddingHorizontal: SIZES.padding * 3,
                paddingVertical: SIZES.padding * 2,
                backgroundColor: COLORS.white,
                elevation: 4,
            }}>
                <TouchableOpacity
                    style={{
                        width: 45,
                        alignItems: 'center',
                        justifyContent: 'center',
                    }}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={{
                            height: 20,
                            width: 20,
                            tintColor: COLORS.black,
                        }}
                    />
                </TouchableOpacity>

                <View style={{
                    flex: 1,
                    alignItems: 'center',
                    justifyContent: 'center',
                }}>
                    <Text style={{
                        color: COLORS.black,
                        ...FONTS.h3,
                    }}>Choose your canteen</Text>
                </View>

                <View style={{
                    width: 45,
                    alignItems: 'center',
                    justifyContent: 'center',
                }} /> {/* Empty space for alignment */}
            </View>
        );
    }

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={{
                flex: 1,
                margin: SIZES.base,
                borderRadius: 20,
                overflow: 'hidden',
                elevation: 3,
            }}
            onPress={() => console.log(item.title)}
        >
            <View style={{
                height: 120,
                borderTopLeftRadius: 20,
                borderTopRightRadius: 20,
                backgroundColor: COLORS.primary,
            }}>
                <Image
                    source={item.img}
                    resizeMode="cover"
                    style={{
                        width: '100%',
                        height: '100%',
                        borderTopLeftRadius: 20,
                        borderTopRightRadius: 20,
                    }}
                />
            </View>

            <View style={{
                padding: SIZES.padding,
                backgroundColor: COLORS.lightGray,
                borderBottomLeftRadius: 20,
                borderBottomRightRadius: 20,
            }}>
                <Text style={{
                    ...FONTS.h4,
                    color: COLORS.black,
                }}>{item.title}</Text>
                <Text style={{
                    ...FONTS.body4,
                    color: COLORS.gray,
                }}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={{
            flex: 1,
            backgroundColor: COLORS.white,
        }}>
            {renderHeader()}
            <FlatList
                contentContainerStyle={{
                    paddingHorizontal: SIZES.padding * 3,
                }}
                numColumns={2}
                columnWrapperStyle={{
                    justifyContent: 'space-between',
                }}
                data={canteens}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={<View style={{ marginBottom: 80 }} />}
            />
        </SafeAreaView>
    );
}

export default Food;
