import React, { useState, useEffect } from "react";
import {
    SafeAreaView,
    View,
    Text,
    Image,
    FlatList,
    TouchableOpacity,
    Alert,
    StyleSheet
} from "react-native";
import { COLORS, SIZES, FONTS, icons, images } from "../constants";
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Home = () => {
    const navigation = useNavigation();
    const [hasUnreadNotifications, setHasUnreadNotifications] = useState(true);

    const featuresData = [
        {
            id: 1,
            icon: icons.reload,
            color: COLORS.primary,
            backgroundColor: COLORS.white,
            description: "Top Up",
            screen: "TopUp"
        },
        {
            id: 2,
            icon: icons.food,
            color: COLORS.yellow,
            backgroundColor: COLORS.lightyellow,
            description: "Foods",
            screen: "Food"
        },
        {
            id: 3,
            icon: icons.diet,
            color: COLORS.primary,
            backgroundColor: COLORS.lightGreen,
            description: "Diet Tracker",
            screen: "DietTracker"
        },
        {
            id: 4,
            icon: icons.wallet,
            color: COLORS.red,
            backgroundColor: COLORS.lightRed,
            description: "Wallet",
            screen: "Wallet"
        },
        {
            id: 5,
            icon: icons.bill,
            color: COLORS.yellow,
            backgroundColor: COLORS.lightyellow,
            description: "Transactions",
            screen: "Transactions"
        },
        {
            id: 6,
            icon: icons.cart,
            color: COLORS.yellow,
            backgroundColor: COLORS.lightyellow,
            description: "My Orders",
            screen: "OrderList"
        },
    ];

    const [features, setFeatures] = useState(featuresData);
    const [featuredFoods, setFeaturedFoods] = useState([]);
    const [balance, setBalance] = useState(null);
    const [loading, setLoading] = useState(true);
    const [csrfToken, setCsrfToken] = useState('');
    const [firstName, setFirstName] = useState('');

    useEffect(() => {
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/csrf/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token', error);
            }
        };

        fetchCsrfToken();
    }, []);

    useEffect(() => {
        const fetchFeaturedFoods = async () => {
            try {
                const accessToken = await AsyncStorage.getItem('accessToken');
                if (!accessToken || !csrfToken) {
                    console.error('Token or CSRF Token not available');
                    return;
                }

                const response = await axios.get('http://127.0.0.1:8000/featured-foods/', {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                        'X-CSRFToken': csrfToken
                    }
                });

                setFeaturedFoods(response.data.slice(0, 4)); // Limit to 4 items
            } catch (error) {
                console.error("Error fetching featured foods:", error);
                Alert.alert('Error', 'Unable to fetch featured foods.');
            } finally {
                setLoading(false);
            }
        };

        if (csrfToken) {
            fetchFeaturedFoods();
        }
    }, [csrfToken]);

    // Fetch Balance and User Information
    const fetchBalance = async () => {
        try {
            const accessToken = await AsyncStorage.getItem('accessToken');
            if (!accessToken || !csrfToken) {
                console.error('Token or CSRF Token not available');
                return;
            }

            const response = await axios.get('http://127.0.0.1:8000/balance/', {
                headers: {
                    'Authorization': `Bearer ${accessToken}`,
                    'X-CSRFToken': csrfToken
                }
            });

            // Convert balance to float and format
            const balance = parseFloat(response.data.balance).toFixed(2);
            const formattedBalance = Number(balance).toLocaleString();
            setBalance(formattedBalance);

            // Capitalize the first letter of the first name
            const firstName = response.data.first_name;
            const capitalizedFirstName = firstName.charAt(0).toUpperCase() + firstName.slice(1);
            setFirstName(capitalizedFirstName);
        } catch (error) {
            console.error("Error fetching balance:", error);
            Alert.alert('Error', 'Unable to fetch balance.');
        }
    };

    useFocusEffect(
        React.useCallback(() => {
            if (csrfToken) {
                fetchBalance();
            }
        }, [csrfToken])
    );

    useEffect(() => {
        const checkNotifications = async () => {
            const notificationsRead = await AsyncStorage.getItem('notificationsRead');
            setHasUnreadNotifications(notificationsRead !== 'true');
        };

        checkNotifications();
    }, []);

    const handleNotificationsPress = async () => {
        await AsyncStorage.setItem('notificationsRead', 'true');
        setHasUnreadNotifications(false);
        navigation.navigate("Notif");
    };

    function renderHeader() {
        return (
            <View style={styles.headerContainer}>
                <View style={styles.logoContainer}>
                    <Image
                        source={images.juanbytesLogo}
                        resizeMode="contain"
                        style={styles.logo}
                    />
                </View>
                <View style={styles.notificationContainer}>
                    <TouchableOpacity
                        style={styles.notificationButton}
                        onPress={handleNotificationsPress}
                    >
                        <Image
                            source={icons.bell}
                            style={styles.notificationIcon}
                        />
                        {hasUnreadNotifications && (
                            <View style={styles.notificationBadge}></View>
                        )}
                    </TouchableOpacity>
                </View>
            </View>
        )
    }

    function renderUserBalance() {
        return (
            <View style={styles.balanceContainer}>
                <View>
                    <Text style={{ ...FONTS.body3, color: COLORS.gray }}>Available Balance</Text>
                    <Text style={{ ...FONTS.h2 }}>₱{balance}</Text>
                </View>
                <View>
                    <TouchableOpacity
                        style={styles.sendMoneyButton}
                        onPress={() => navigation.navigate("SendMoney")}
                    >
                        <Image
                            source={icons.send}
                            resizeMode="contain"
                            style={styles.sendMoneyIcon}
                        />
                    </TouchableOpacity>
                </View>
            </View>
        )
    }

    function renderFeatures() {
        const Header = () => (
            <View style={styles.sectionHeader}>
                <Text style={{ ...FONTS.h3 }}>Features</Text>
            </View>
        );

        const renderItem = ({ item }) => (
            <TouchableOpacity
                style={styles.featureItem}
                onPress={() => {
                    if (item.screen) {
                        navigation.navigate(item.screen);
                    } else {
                        console.log(item.description);
                    }
                }}
            >
                <View style={[styles.featureIconContainer, { backgroundColor: item.backgroundColor }]}>
                    <Image
                        source={item.icon}
                        resizeMode="contain"
                        style={[styles.featureIcon, { tintColor: item.color }]}
                    />
                </View>
                <Text style={styles.featureDescription}>{item.description}</Text>
            </TouchableOpacity>
        );

        return (
            <FlatList
                ListHeaderComponent={Header}
                data={features}
                numColumns={4}
                columnWrapperStyle={styles.featureColumnWrapper}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                contentContainerStyle={styles.featuresList}
            />
        )
    }

    function renderFeaturedFoods() {
        const Header = () => (
            <View style={styles.sectionHeader}>
                <Text style={{ ...FONTS.h3 }}>Featured Foods</Text>
            </View>
        );

        const renderItem = ({ item }) => (
            <TouchableOpacity
                style={styles.foodItem}
                onPress={() => navigation.navigate('FoodDetails', { item })}
            >
                <View style={styles.foodImageContainer}>
                    <Image
                        source={images.promoBanner}
                        resizeMode="cover"
                        style={styles.foodImage}
                    />
                </View>
                <Text style={styles.foodName}>{item.name}</Text>
                <Text style={styles.foodPrice}>₱{item.price}</Text>
            </TouchableOpacity>
        );

        return (
            <FlatList
                ListHeaderComponent={Header}
                data={featuredFoods}
                numColumns={2}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                contentContainerStyle={styles.featuredFoodsContainer}
            />
        )
    }

    return (
        <SafeAreaView style={styles.safeAreaView}>
            {renderHeader()}
            <FlatList
                data={[]}
                keyExtractor={(item, index) => index.toString()}
                showsVerticalScrollIndicator={false}
                ListHeaderComponent={
                    <View>
                        {renderUserBalance()}
                        {renderFeatures()}
                        {renderFeaturedFoods()}
                    </View>
                }
            />
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    safeAreaView: {
        flex: 1,
        backgroundColor: COLORS.white,
        padding: SIZES.padding * 2, // Adjust padding as needed
    },
    headerContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: SIZES.padding,
        paddingBottom: SIZES.padding * 2,
