import React, { useState } from "react";
import { SafeAreaView, View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from "react-native";
import { COLORS, SIZES, FONTS, icons } from "../constants";

const TopUp = ({ navigation }) => {
    const [depositAmount, setDepositAmount] = useState('');
    const [paymentAmount, setPaymentAmount] = useState('');

    // Sample values
    const sampleDepositAmount = 1000; // Amount user wants to deposit
    const samplePaymentAmount = 1050; // Amount user will pay to the finance officer (including fees or additional charges)

    function renderHeader() {
        return (
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={styles.icon}
                    />
                </TouchableOpacity>

                <View style={styles.titleContainer}>
                    <Text style={styles.title}>TopUp</Text>
                </View>

                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => console.log("Settings")}
                >
                    <Image
                        source={icons.settings}
                        style={styles.icon}
                    />
                </TouchableOpacity>
            </View>
        );
    }

    function renderTopUpForm() {
        return (
            <View style={styles.formContainer}>
                <Text style={styles.formTitle}>Top-Up Money</Text>
                <TextInput
                    style={styles.input}
                    placeholder="Enter Amount to Deposit"
                    keyboardType="numeric"
                    value={depositAmount}
                    onChangeText={text => {
                        setDepositAmount(text);
                        // Assuming a fixed fee percentage for demo purposes
                        const fee = 0.05;
                        setPaymentAmount((parseFloat(text) * (1 + fee)).toFixed(2));
                    }}
                />
                <TextInput
                    style={styles.input}
                    placeholder="Total Amount to Pay"
                    keyboardType="numeric"
                    value={paymentAmount}
                    editable={false}
                />
                <TouchableOpacity
                    style={styles.button}
                    onPress={() => console.log("Submit Top-Up")}
                >
                    <Text style={styles.buttonText}>Pay</Text>
                </TouchableOpacity>
            </View>
        );
    }

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            {renderTopUpForm()}
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    icon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black,
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    formContainer: {
        paddingHorizontal: SIZES.padding * 3,
        flex: 1,
        // justifyContent: 'center',
    },
    formTitle: {
        ...FONTS.h3,
        color: COLORS.black,
        marginBottom: SIZES.padding * 2,
    },
    input: {
        height: 50,
        borderColor: COLORS.gray,
        borderWidth: 1,
        borderRadius: 10,
        marginBottom: SIZES.base,
        paddingHorizontal: SIZES.padding,
        fontSize: SIZES.body3,
        color: COLORS.black,
    },
    button: {
        backgroundColor: COLORS.primary,
        paddingVertical: SIZES.base,
        borderRadius: 10,
        alignItems: 'center',
    },
    buttonText: {
        color: COLORS.white,
        ...FONTS.h4,
    },
});

export default TopUp;
