import React from "react";
import {
    View,
    Text,
    TouchableOpacity,
    TouchableWithoutFeedback,
    Image,
    TextInput,
    Modal,
    FlatList,
    KeyboardAvoidingView,
    ScrollView,
    Platform,
    Alert
} from "react-native"
import { LinearGradient } from 'expo-linear-gradient'

import { COLORS, SIZES, FONTS, icons, images } from "../constants"

const SignUp = ({ navigation }) => {

    const [showPassword, setShowPassword] = React.useState(false)
    const [mpin, setMpin] = React.useState('');
    const [showMpin, setShowMpin] = React.useState(false);
    const [areas, setAreas] = React.useState([])
    const [selectedArea, setSelectedArea] = React.useState(null)
    const [modalVisible, setModalVisible] = React.useState(false)
    const [fullName, setFullName] = React.useState('');
    const [phoneNumber, setPhoneNumber] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [password2, setPassword2] = React.useState('');
    const [showConfirmPassword, setShowConfirmPassword] = React.useState(true);



    React.useEffect(() => {
        fetch("https://restcountries.com/v3.1/all?fields=name,flags,cca3,idd")
            .then(response => response.json())
            .then(data => {
                let areaData = data.map(item => {
                    return {
                        code: item.cca3,
                        name: item.name?.common,
                        flag: item.flags.png,
                        callingCode: item.idd?.root + item.idd?.suffixes[0],
                    }
                })
                setAreas(areaData)

                if (areaData.length > 0) {
                    let defaultData = areaData.filter(a => a.code == "US")

                    if (defaultData.length > 0) {
                        setSelectedArea(defaultData[0])
                    }
                }
            })
    }, [])

    function handleSignUp() {
        // Validation
        if (!fullName || !phoneNumber || !password || password !== password2) {
            Alert.alert("Validation Error", "Please fill out all fields and ensure passwords match.");
            return;
        }

        // Prepare data
        const userData = {
            phone_number: phoneNumber,
            first_name: fullName.split(' ')[0], // Assuming first name is the first word
            last_name: fullName.split(' ').slice(1).join(' '), // Remaining part as last name
            password: password,
            password2: password2,
        };

        // Fetch CSRF token
        fetch('http://localhost:8000/csrf/')
            .then(response => response.json())
            .then(data => {
                const csrfToken = data.csrfToken;

                // Make POST request
                fetch('http://localhost:8000/register/', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'X-CSRFToken': csrfToken,
                    },
                    body: JSON.stringify(userData),
                })
                .then(response => {
                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }
                    return response.json();
                })
                .then(data => {
                    if (data?.detail) {
                        Alert.alert("Registration Error", data.detail);
                    } else {
                        Alert.alert("Success", "Registration successful!");
                        navigation.navigate("HomeTabs");
                    }
                })
                .catch(error => {
                    Alert.alert("Error", `An error occurred: ${error.message}`);
                    console.error('Fetch error:', error);
                });
            })
            .catch(error => {
                Alert.alert("Error", `An error occurred while fetching CSRF token: ${error.message}`);
                console.error('CSRF fetch error:', error);
            });
    }




    function renderHeader() {
        return (
            <TouchableOpacity
                style={{
                    flexDirection: 'row',
                    alignItems: "center",
                    marginTop: SIZES.padding * 6,
                    paddingHorizontal: SIZES.padding * 2
                }}
                onPress={() => navigation.navigate('Login')}
            >
                <Image
                    source={icons.back}
                    resizeMode="contain"
                    style={{
                        width: 20,
                        height: 20,
                        tintColor: COLORS.black
                    }}
                />

                <Text style={{ marginLeft: SIZES.padding * 1.5, color: COLORS.black, ...FONTS.h4 }}>Login</Text>
            </TouchableOpacity>
        )
    }

    function renderLogo() {
        return (
            <View
                style={{
                    marginTop: SIZES.padding * 5,
                    height: 100,
                    alignItems: 'center',
                    justifyContent: 'center'
                }}
            >
                <Image
                    source={images.juanbytesLogo}
                     resizeMode="contain"
                        style={{
                            width: "100%",
                            height: "100%",
                            borderTopLeftRadius: 20,
                            borderTopRightRadius: 20
                        }}
                />
            </View>
        )
    }

    function renderForm() {
    return (
        <View
            style={{
                marginTop: SIZES.padding * 3,
                marginHorizontal: SIZES.padding * 3,
            }}
        >
            {/* Full Name */}
            <View style={{ marginTop: SIZES.padding * 3 }}>
                <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>Full Name</Text>
                <TextInput
                    style={{
                        marginVertical: SIZES.padding,
                        borderBottomColor: COLORS.black,
                        borderBottomWidth: 1,
                        height: 40,
                        color: COLORS.black,
                        ...FONTS.body3
                    }}
                    placeholder="Enter Full Name"
                    placeholderTextColor={COLORS.black}
                    selectionColor={COLORS.black}
                    value={fullName}
                    onChangeText={text => setFullName(text)}
                />
            </View>

            {/* Phone Number */}
            <View style={{ marginTop: SIZES.padding * 2 }}>
                <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>Phone Number</Text>
                <TextInput
                    style={{
                        marginVertical: SIZES.padding,
                        borderBottomColor: COLORS.black,
                        borderBottomWidth: 1,
                        height: 40,
                        color: COLORS.black,
                        ...FONTS.body3
                    }}
                    placeholder="Enter Phone Number"
                    placeholderTextColor={COLORS.black}
                    selectionColor={COLORS.black}
                    value={phoneNumber}
                    onChangeText={text => setPhoneNumber(text)}
                />
            </View>

            {/* Password */}
            <View style={{ marginTop: SIZES.padding * 2 }}>
                <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>Password</Text>
                <View style={{ position: 'relative' }}>
                    <TextInput
                        style={{
                            marginVertical: SIZES.padding,
                            borderBottomColor: COLORS.black,
                            borderBottomWidth: 1,
                            height: 40,
                            color: COLORS.black,
                            ...FONTS.body3
                        }}
                        placeholder="Enter Password"
                        placeholderTextColor={COLORS.black}
                        selectionColor={COLORS.black}
                        secureTextEntry={!showPassword}
                        value={password}
                        onChangeText={text => setPassword(text)}
                    />
                    <TouchableOpacity
                        style={{
                            position: 'absolute',
                            right: 0,
                            bottom: 10,
                            height: 30,
                            width: 30
                        }}
                        onPress={() => setShowPassword(!showPassword)}
                    >
                        <Image
                            source={showPassword ? icons.disable_eye : icons.eye}
                            style={{
                                height: 20,
                                width: 20,
                                tintColor: COLORS.black
                            }}
                        />
                    </TouchableOpacity>
                </View>
            </View>

            {/* Confirm Password */}
            <View style={{ marginTop: SIZES.padding * 2 }}>
                <Text style={{ color: COLORS.lightorrange, ...FONTS.body3 }}>Confirm Password</Text>
                <View style={{ position: 'relative' }}>
                    <TextInput
                        style={{
                            marginVertical: SIZES.padding,
                            borderBottomColor: COLORS.black,
                            borderBottomWidth: 1,
                            height: 40,
                            color: COLORS.black,
                            ...FONTS.body3
                        }}
                        placeholder="Confirm Password"
                        placeholderTextColor={COLORS.black}
                        selectionColor={COLORS.black}
                        secureTextEntry={!showConfirmPassword}
                        value={password2}
                        onChangeText={text => setPassword2(text)}
                    />
                    <TouchableOpacity
                        style={{
                            position: 'absolute',
                            right: 0,
                            bottom: 10,
                            height: 30,
                            width: 30
                        }}
                        onPress={() => setShowConfirmPassword(!showConfirmPassword)}
                    >
                        <Image
                            source={showConfirmPassword ? icons.disable_eye : icons.eye}
                            style={{
                                height: 20,
                                width: 20,
                                tintColor: COLORS.black
                            }}
                        />
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    );
}


    function renderButton() {
        return (
            <View style={{ margin: SIZES.padding * 3 }}>
                <TouchableOpacity
                    style={{
                        height: 60,
                        backgroundColor: COLORS.black,
                        borderRadius: SIZES.radius / 1.5,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                    onPress={handleSignUp}
                >
                    <Text style={{ color: COLORS.white, ...FONTS.h3 }}>Continue</Text>
                </TouchableOpacity>
            </View>
        );
    }

    function renderAreaCodesModal() {

        const renderItem = ({ item }) => {
            return (
                <TouchableOpacity
                    style={{ padding: SIZES.padding, flexDirection: 'row' }}
                    onPress={() => {
                        setSelectedArea(item)
                        setModalVisible(false)
                    }}
                >
                    <Image
                        source={{ uri: item.flag }}
                        style={{
                            width: 30,
                            height: 30,
                            marginRight: 10
                        }}
                    />
                    <Text style={{ ...FONTS.body4 }}>{item.name}</Text>
                </TouchableOpacity>
            )
        }

        return (
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
            >
                <TouchableWithoutFeedback
                    onPress={() => setModalVisible(false)}
                >
                    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                        <View
                            style={{
                                height: 400,
                                width: SIZES.width * 0.8,
                                backgroundColor: COLORS.white,
                                borderRadius: SIZES.radius
                            }}
                        >
                            <FlatList
                                data={areas}
                                renderItem={renderItem}
                                keyExtractor={(item) => item.code}
                                showsVerticalScrollIndicator={false}
                                style={{
                                    padding: SIZES.padding * 2,
                                    marginBottom: SIZES.padding * 2
                                }}
                            />
                        </View>
                    </View>
                </TouchableWithoutFeedback>
            </Modal>
        )
    }

    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === "ios" ? "padding" : null}
            style={{ flex: 1 }}
        >
            <LinearGradient
                colors={[COLORS.white, COLORS.white]}
                style={{ flex: 1 }}
            >
                <ScrollView>
                    {renderHeader()}
                    {renderLogo()}
                    {renderForm()}
                    {renderButton()}
                </ScrollView>
            </LinearGradient>
            {renderAreaCodesModal()}
        </KeyboardAvoidingView>
    )
}

export default SignUp;
