import React, { useState, useEffect } from "react";
import {
    SafeAreaView,
    View,
    Text,
    Image,
    FlatList,
    TouchableOpacity
} from "react-native";
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const Food = () => {
    const navigation = useNavigation();
    const [canteens, setCanteens] = useState([]);
    const [loading, setLoading] = useState(true);
    const [csrfToken, setCsrfToken] = useState('');

    // Fetch CSRF Token
    useEffect(() => {
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/csrf/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token', error);
            }
        };

        fetchCsrfToken();
    }, []); // Empty dependency array to ensure this runs only once

    const fetchCanteens = async () => {
        try {
            const accessToken = await AsyncStorage.getItem('accessToken');
            if (!accessToken || !csrfToken) {
                console.error('Access token or CSRF token not available');
                return;
            }

            const response = await axios.get('http://127.0.0.1:8000/canteens/', {
                headers: {
                    'Authorization': `Bearer ${accessToken}`,
                    'X-CSRFToken': csrfToken
                }
            });
            setCanteens(response.data);
        } catch (error) {
            console.error("Error fetching canteens:", error);
        }
    };

    useFocusEffect(
        React.useCallback(() => {
            if (csrfToken) {
                fetchCanteens();
            }
        }, [csrfToken])
    );

    const renderHeader = () => (
        <View style={{ flexDirection: 'row', marginVertical: SIZES.padding * 3 }}>
            <View style={{ flex: 1 }}>
                <Image
                    source={images.juanbytesLogo}
                    resizeMode="contain"
                    style={{ width: 100, height: 100 }}
                />
            </View>
        </View>
    );

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={{
                flexDirection: 'row',
                padding: SIZES.padding,
                marginBottom: SIZES.padding,
                backgroundColor: COLORS.lightGray,
                borderRadius: 10
            }}
        >
            <Image
                source={item.image}
                resizeMode="contain"
                style={{ width: 50, height: 50, marginRight: SIZES.padding }}
            />
            <View style={{ justifyContent: 'center' }}>
                <Text style={{ ...FONTS.h3 }}>{item.name}</Text>
                <Text style={{ ...FONTS.body4, color: COLORS.gray }}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={{ flex: 1, backgroundColor: COLORS.white }}>
            <FlatList
                data={canteens}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                ListHeaderComponent={renderHeader}
                contentContainerStyle={{ paddingHorizontal: SIZES.padding }}
                showsVerticalScrollIndicator={false}
            />
        </SafeAreaView>
    );
};

export default Food;
