import React, { useState, useEffect } from "react";
import { Alert, SafeAreaView, View, Text, TouchableOpacity, StyleSheet, FlatList } from "react-native";
import { COLORS, SIZES, FONTS } from "../constants";
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';

const TopUp = ({ navigation }) => {
    const [balance, setBalance] = useState(null);
    const [amount, setAmount] = useState('');
    const [topUpStatus, setTopUpStatus] = useState(null);
    const [topUpRequests, setTopUpRequests] = useState([]);
    const [csrfToken, setCsrfToken] = useState('');
    const [loading, setLoading] = useState(false);
    const [userId, setUserId] = useState(null); // New state for user ID

    useEffect(() => {
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/csrf/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token', error);
            }
        };

        fetchCsrfToken();
    }, []);

    useEffect(() => {
        const fetchUserId = async () => {
            try {
                const accessToken = await AsyncStorage.getItem('accessToken');
                if (!accessToken || !csrfToken) {
                    console.error('Token or CSRF Token not available');
                    return;
                }

                const response = await axios.get('http://127.0.0.1:8000/details/', {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                        'X-CSRFToken': csrfToken
                    }
                });

                setUserId(response.data.id); // Set user ID from user details
            } catch (error) {
                console.error("Error fetching user details:", error);
                Alert.alert('Error', 'Unable to fetch user details.');
            }
        };

        if (csrfToken) {
            fetchUserId();
        }
    }, [csrfToken]);

    useEffect(() => {
        if (userId) {
            const fetchTopUpRequests = async () => {
                try {
                    const response = await axios.get(`http://127.0.0.1:8000/topups/`, {
                        headers: {
                            'Authorization': `Bearer ${await AsyncStorage.getItem('accessToken')}`,
                            'X-CSRFToken': csrfToken
                        }
                    });
                    // Filter top-up requests for the specific user ID
                    setTopUpRequests(response.data.filter(request => request.user_id === userId));
                } catch (error) {
                    console.error("Error fetching top-up requests:", error);
                }
            };

            fetchTopUpRequests();
        }
    }, [userId, csrfToken]);

    const handleTopUp = async () => {
        // Handle top-up logic
    };

    function renderHeader() {
        return (
            <View style={styles.header}>
                <TouchableOpacity style={styles.iconButton} onPress={() => navigation.goBack()}>
                    <Image source={icons.back} style={styles.icon} />
                </TouchableOpacity>
                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Top Up</Text>
                </View>
            </View>
        );
    }

    function renderTopUpForm() {
        const canPay = amount && balance > 0; // Adjust logic based on requirements

        return (
            <View style={styles.formContainer}>
                <Text style={styles.formTitle}>Top Up Form</Text>
                <TextInput
                    style={styles.input}
                    placeholder="Enter Amount"
                    keyboardType="numeric"
                    value={amount}
                    onChangeText={setAmount}
                />
                <View style={styles.amountContainer}>
                    <Text style={styles.amountLabel}>Amount:</Text>
                    <Text style={styles.amountValue}>₱{amount}</Text>
                </View>
                {topUpStatus && (
                    <View style={styles.statusContainer}>
                        <Text style={styles.statusText}>{topUpStatus}</Text>
                    </View>
                )}
                <TouchableOpacity
                    style={[styles.button, !canPay && styles.buttonDisabled]}
                    onPress={handleTopUp}
                    disabled={!canPay}
                >
                    <Text style={styles.buttonText}>{loading ? "Processing..." : "Pay"}</Text>
                </TouchableOpacity>
            </View>
        );
    }

    function renderTopUpRequests() {
        const renderItem = ({ item }) => (
            <View style={styles.requestContainer} key={item.id ? item.id.toString() : item.toString()}>
                <Text style={styles.requestText}>Amount: ₱{item.amount}</Text>
                <Text style={styles.requestText}>
                    Date: {new Date(item.created_at).toLocaleDateString()} 
                    {' '}
                    {new Date(item.created_at).toLocaleTimeString()} {/* Added time */}
                </Text>
                <Text style={styles.requestText}>Status: {item.is_approved ? "Approved" : "Pending"}</Text>
            </View>
        );
    
        return (
            <View style={styles.requestsSection}>
                <FlatList
                    data={topUpRequests}
                    renderItem={renderItem}
                    keyExtractor={(item) => item.id ? item.id.toString() : Math.random().toString()}
                    contentContainerStyle={styles.requestsList}
                />
            </View>
        );
    }
    
    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            <View style={styles.balanceContainer}>
                <Text style={styles.balanceLabel}>Available Balance:</Text>
                <Text style={styles.balanceValue}>₱{balance || "0.00"}</Text>
            </View>
            {renderTopUpForm()}
            {renderTopUpRequests()}
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5, // Reduced margin
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    icon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black,
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    formContainer: {
        marginVertical: SIZES.padding, // Adjusted margin
        paddingHorizontal: SIZES.padding * 3,
    },
    formTitle: {
        ...FONTS.h4,
        color: COLORS.black,
        marginBottom: SIZES.base,
    },
    input: {
        height: 50,
        borderColor: COLORS.gray,
        borderWidth: 1,
        borderRadius: 10,
        marginBottom: SIZES.base,
        paddingHorizontal: SIZES.padding,
        fontSize: SIZES.body3,
        color: COLORS.black,
    },
    amountContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginVertical: SIZES.base,
        padding: SIZES.padding,
        borderRadius: 10,
        backgroundColor: COLORS.lightGray,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 3 },
        shadowOpacity: 0.27,
        shadowRadius: 4.65,
        elevation: 6,
    },
    amountLabel: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    amountValue: {
        ...FONTS.h4,
        color: COLORS.black,
    },
    button: {
        backgroundColor: COLORS.primary,
        paddingVertical: SIZES.base,
        borderRadius: 10,
        alignItems: 'center',
    },
    buttonDisabled: {
        backgroundColor: COLORS.gray,
    },
    buttonText: {
        color: COLORS.white,
        ...FONTS.h4,
    },
    statusContainer: {
        marginVertical: SIZES.base,
        padding: SIZES.padding,
        borderRadius: 10,
        backgroundColor: COLORS.lightGray,
        alignItems: 'center',
    },
    statusText: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    balanceContainer: {
        paddingHorizontal: SIZES.padding * 3,
        paddingVertical: SIZES.padding,
        borderBottomWidth: 1,
        borderBottomColor: COLORS.gray,
        backgroundColor: COLORS.lightGray,
        alignItems: 'center',
    },
    balanceLabel: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    balanceValue: {
        ...FONTS.h4,
        color: COLORS.black,
    },
    requestContainer: {
        padding: SIZES.padding,
        marginBottom: SIZES.base,
        borderRadius: 10,
        backgroundColor: COLORS.lightGray,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 3 },
        shadowOpacity: 0.27,
        shadowRadius: 4.65,
        elevation: 6,
    },
    requestText: {
        ...FONTS.body4,
        color: COLORS.gray,
        marginBottom: SIZES.base / 2,
    },
    requestsList: {
        paddingHorizontal: SIZES.padding * 1,
    },
    requestsSection: {
        marginVertical: SIZES.padding, // Reduced margin
        paddingHorizontal: SIZES.padding * 3,
    },
});

export default TopUp;
