import React, { useState, useEffect } from "react";
import { SafeAreaView, View, Text, TouchableOpacity, StyleSheet, FlatList, ActivityIndicator, Alert, Modal, TextInput } from "react-native";
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import { COLORS, SIZES, FONTS } from "../../../constants";
import axios from 'axios';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const OrderList = () => {
    const navigation = useNavigation();
    const [loading, setLoading] = useState(false);
    const [orders, setOrders] = useState([]);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [password, setPassword] = useState('');
    const [modalVisible, setModalVisible] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const fetchOrders = async () => {
            setLoading(true);
            try {
                const accessToken = await AsyncStorage.getItem('accessToken');
                if (!accessToken) {
                    console.error('Access token not available');
                    return;
                }

                const response = await axios.get('http://127.0.0.1:8000/orders/', {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                    }
                });

                console.log(response.data); // Add this line to check the response
                setOrders(response.data);

                const userResponse = await axios.get('http://127.0.0.1:8000/details/', {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                    }
                });

                setUser(userResponse.data);
            } catch (error) {
                console.error("Error fetching orders:", error);
                Alert.alert("Error", "There was an error fetching the orders.");
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    const handlePayNow = (order) => {
        setSelectedOrder(order);
        setModalVisible(true);
    };

    const handleTransfer = async () => {
        if (!password) {
            Alert.alert("Error", "Please enter your password.");
            return;
        }

        try {
            const accessToken = await AsyncStorage.getItem('accessToken');
            if (!accessToken) {
                console.error('Access token not available');
                return;
            }

            // Verify the password
            const passwordResponse = await axios.post('http://127.0.0.1:8000/verify-password/', { password }, {
                headers: {
                    'Authorization': `Bearer ${accessToken}`,
                },
            });

            if (passwordResponse.status === 200) {
                const response = await axios.post('http://127.0.0.1:8000/transfer/', {
                    recipient_phone_number: selectedOrder.vendor_phone_number,
                    amount: selectedOrder.total_price,
                }, {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                    }
                });

                // Update order payment status
                await axios.patch(`http://127.0.0.1:8000/orders/${selectedOrder.id}/pay/`, {}, {
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                    }
                });

                Alert.alert("Success", "Payment successful.");
                setModalVisible(false);
                setOrders((prevOrders) => prevOrders.map((order) =>
                    order.id === selectedOrder.id ? { ...order, is_paid: true } : order
                ));
            } else {
                Alert.alert("Error", "Incorrect password.");
            }
        } catch (error) {
            console.error("Error processing payment:", error);
            Alert.alert("Error", "There was an error processing the payment.");
        }
    };

    const renderItem = ({ item }) => {
        const isBuyer = user && item.user === user.email;
        const isVendor = user && item.vendor === user.email;

        return (
            <View style={styles.orderItem}>
                {item.food && (
                    <>
                        <Text style={styles.foodName}>{item.food.name}</Text>
                        <Text style={styles.foodDescription}>{item.food.description}</Text>
                        <Text style={styles.foodPrice}>₱{item.food.price}</Text>
                    </>
                )}
                <Text style={styles.orderQuantity}>Quantity: {item.quantity}</Text>
                <Text style={styles.totalPrice}>Total: ₱{item.total_price}</Text>
                <Text style={styles.orderedBy}>Ordered by: {item.user}</Text>
                <Text style={styles.orderDate}>Ordered on: {new Date(item.created_at).toLocaleDateString()}</Text>
                {isBuyer && !item.is_paid && (
                    <TouchableOpacity style={styles.payButton} onPress={() => handlePayNow(item)}>
                        <Text style={styles.payButtonText}>Pay Now</Text>
                    </TouchableOpacity>
                )}
                {isVendor && <Text style={styles.vendorViewText}>Vendor View</Text>}
                {item.is_paid && <Text style={styles.paidText}>Paid</Text>}
            </View>
        );
    };

    if (loading) {
        return (
            <SafeAreaView style={styles.container}>
                <View style={styles.loader}>
                    <ActivityIndicator size="large" color={COLORS.primary} />
                </View>
            </SafeAreaView>
        );
    }

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.header}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.navigate("Home")}
                >
                    <Icon name="arrow-left" size={20} color={COLORS.black} />
                </TouchableOpacity>
                <View style={styles.titleContainer}>
                    <Text style={styles.title}>My Orders</Text>
                </View>
            </View>
            <FlatList
                data={orders}
                renderItem={renderItem}
                keyExtractor={(item) => item.id.toString()}
                contentContainerStyle={styles.contentContainer}
                ListEmptyComponent={<Text style={styles.emptyText}>No orders found</Text>}
                numColumns={2}
            />
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => setModalVisible(false)}
            >
                <View style={styles.modalContainer}>
                    <View style={styles.modalView}>
                        <Text style={styles.modalTitle}>Confirm Payment</Text>
                        <Text style={styles.modalText}>Vendor Email: {selectedOrder?.vendor}</Text>
                        <Text style={styles.modalText}>Vendor Phone no.: {selectedOrder?.vendor_phone_number}</Text>
                        <Text style={styles.modalText}>Enter your password to confirm the payment of ₱{selectedOrder?.total_price}</Text>
                        <TextInput
                            style={styles.input}
                            placeholder="Enter your password"
                            secureTextEntry
                            value={password}
                            onChangeText={setPassword}
                        />
                        <View style={styles.modalButtons}>
                            <TouchableOpacity style={styles.modalButton} onPress={handleTransfer}>
                                <Text style={styles.modalButtonText}>Confirm</Text>
                            </TouchableOpacity>
                            <TouchableOpacity style={styles.modalButton} onPress={() => setModalVisible(false)}>
                                <Text style={styles.modalButtonText}>Cancel</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </View>
            </Modal>
        </SafeAreaView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    header: {
        flexDirection: 'row',
        marginVertical: SIZES.padding * 5,
        paddingHorizontal: SIZES.padding * 3,
        alignItems: 'center',
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    contentContainer: {
        paddingHorizontal: SIZES.padding * 3,
    },
    orderItem: {
        backgroundColor: COLORS.lightGray,
        borderRadius: 10,
        padding: SIZES.padding,
        marginVertical: SIZES.padding * 0.5,
        flex: 1,
        marginHorizontal: SIZES.padding * 0.5,
    },
    foodName: {
        ...FONTS.h2,
        color: COLORS.black,
    },
    foodDescription: {
        ...FONTS.body3,
        color: COLORS.gray,
        marginVertical: SIZES.padding * 0.5,
    },
    foodPrice: {
        ...FONTS.h2,
        color: COLORS.black,
    },
    foodVendor: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    orderQuantity: {
        ...FONTS.body3,
        color: COLORS.black,
        marginVertical: SIZES.padding * 0.5,
    },
    totalPrice: {
        ...FONTS.h2,
        color: COLORS.primary,
    },
    orderDate: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    orderedBy: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    payButton: {
        backgroundColor: COLORS.primary,
        borderRadius: 5,
        padding: SIZES.padding,
        alignItems: 'center',
        marginTop: SIZES.padding,
    },
    payButtonText: {
        color: COLORS.white,
        ...FONTS.h3,
    },
    paidText: {
        color: COLORS.green,
        ...FONTS.h3,
        marginTop: SIZES.padding,
    },
    loader: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    emptyText: {
        textAlign: 'center',
        ...FONTS.body3,
        color: COLORS.gray,
        marginTop: SIZES.padding * 2,
    },
    modalContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0,0,0,0.5)',
    },
    modalView: {
        width: '80%',
        backgroundColor: COLORS.white,
        borderRadius: 20,
        padding: SIZES.padding,
        alignItems: 'center',
    },
    modalTitle: {
        ...FONTS.h2,
        color: COLORS.black,
        marginBottom: SIZES.padding,
    },
    modalText: {
        ...FONTS.body3,
        color: COLORS.black,
        marginBottom: SIZES.padding,
    },
    input: {
        width: '100%',
        height: 40,
        borderColor: COLORS.gray,
        borderWidth: 1,
        borderRadius: 5,
        paddingHorizontal: SIZES.padding,
        marginBottom: SIZES.padding,
    },
    modalButtons: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '100%',
    },
    modalButton: {
        flex: 1,
        backgroundColor: COLORS.primary,
