import React from "react";
import { SafeAreaView, View, Text, FlatList, Image, TouchableOpacity, StyleSheet } from "react-native";
import { COLORS, SIZES, FONTS, icons, images } from "../constants";

const Food = ({ navigation }) => {
    const canteens = [
        {
            id: 1,
            img: images.promoBanner, // Replace with specific canteen images
            title: "APS Canteen",
            description: "Enjoy a variety of delicious meals!"
        },
        {
            id: 2,
            img: images.promoBanner, // Replace with specific canteen images
            title: "PGN Canteen",
            description: "Grab your favorite snacks and meals!"
        },
        {
            id: 3,
            img: images.promoBanner, // Replace with specific canteen images
            title: "GGN Canteen",
            description: "Fresh and tasty options available!"
        },
        {
            id: 4,
            img: images.promoBanner, // Replace with specific canteen images
            title: "Yellow Canteen",
            description: "Diverse menu options for everyone!"
        },
    ];

    function renderHeader() {
        return (
            <View style={styles.headerContainer}>
                <TouchableOpacity
                    style={styles.iconButton}
                    onPress={() => navigation.goBack()}
                >
                    <Image
                        source={icons.back}
                        style={styles.icon}
                    />
                </TouchableOpacity>

                <View style={styles.titleContainer}>
                    <Text style={styles.title}>Choose your canteen</Text>
                </View>

                <View style={styles.iconButton} /> {/* Empty space for alignment */}
            </View>
        );
    }

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={styles.itemContainer}
            onPress={() => console.log(item.title)}
        >
            <View style={styles.imageContainer}>
                <Image
                    source={item.img}
                    resizeMode="cover"
                    style={styles.image}
                />
            </View>

            <View style={styles.infoContainer}>
                <Text style={styles.itemTitle}>{item.title}</Text>
                <Text style={styles.itemDescription}>{item.description}</Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            <FlatList
                contentContainerStyle={styles.listContainer}
                numColumns={2}
                columnWrapperStyle={styles.columnWrapper}
                data={canteens}
                keyExtractor={item => `${item.id}`}
                renderItem={renderItem}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={<View style={styles.footer} />}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.white,
    },
    headerContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingHorizontal: SIZES.padding * 3,
        paddingVertical: SIZES.padding * 2,
        backgroundColor: COLORS.white,
        elevation: 4,
    },
    iconButton: {
        width: 45,
        alignItems: 'center',
        justifyContent: 'center',
    },
    icon: {
        height: 20,
        width: 20,
        tintColor: COLORS.black,
    },
    titleContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        color: COLORS.black,
        ...FONTS.h3,
    },
    itemContainer: {
        flex: 1,
        margin: SIZES.base,
        borderRadius: 20,
        overflow: 'hidden',
        elevation: 3,
    },
    imageContainer: {
        height: 120,
        borderTopLeftRadius: 20,
        borderTopRightRadius: 20,
        backgroundColor: COLORS.primary,
    },
    image: {
        width: '100%',
        height: '100%',
        borderTopLeftRadius: 20,
        borderTopRightRadius: 20,
    },
    infoContainer: {
        padding: SIZES.padding,
        backgroundColor: COLORS.lightGray,
        borderBottomLeftRadius: 20,
        borderBottomRightRadius: 20,
    },
    itemTitle: {
        ...FONTS.h4,
        color: COLORS.black,
    },
    itemDescription: {
        ...FONTS.body4,
        color: COLORS.gray,
    },
    listContainer: {
        paddingHorizontal: SIZES.padding * 3,
    },
    columnWrapper: {
        justifyContent: 'space-between',
    },
    footer: {
        marginBottom: 80,
    },
});

export default Food;
